import React, {useState, useEffect} from 'react';
import api from '../utils/Api.js';
import Card from './Card.js'

function Main({onEditProfile, onAddPlace, onEditAvatar, onCardClick} ) {

    const [userName, setUserName] = useState('');
    const [userDescription, setUserDescription] = useState('');
    const [userAvatar, setUserAvatar] = useState('');
    const [cards, setCards] = useState([]);

    useEffect(() => {
        Promise.all([api.getUserInfo(), api.getCards()])
        .then(([userData, cardsData]) => {
            setUserName(userData.name);
            setUserDescription(userData.about);
            setUserAvatar(userData.avatar);
            setCards(cardsData);
        })
        .catch(err => {
            console.log(err)
        });
    }, [])

  return (
    <body className='page'>
      <main className="content">
    <section className="profile">
        <div className="profile__info">
            <div className="profile__user" onClick={onEditAvatar}>
                <img src={userAvatar}  className="profile__avatar" alt="аватар"/>
            </div>
            <div className="profile__info">
                <div className="profile__info-edit">
                    <h1 className="profile__name">{userName}</h1>
                    <button className="profile__button-edit-name" onClick={onEditProfile} aria-label="Open" type="button"></button>
                </div>
                <p className="profile__description">{userDescription}</p>
            </div>
        </div>
        <button className="profile__button-image-add" onClick={onAddPlace} aria-label="PlusCard" type="button"></button>
    </section>

    <section className="elements">
    {
					cards.map(card => (
						<Card card={card} key={card._id} onCardClick={onCardClick} />
					))
				}
    </section>

      </main>
    </body>
  );

}



export default Main;
